package conf

import (
	"flag"
)

var prime = flag.String("prime", "", "")

type EncryptConf struct {
	Prime string
}

func (c *EncryptConf) InitConf() *EncryptConf {
	c.Prime = *prime
	return c
}

// 这个方法支持用户直接通过参数传入一个
func NewEncryptConf(prime string) *EncryptConf {
	return &EncryptConf{
		Prime: prime,
	}
}
